package telusko;

public class while_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1;
		while (i<=5) {
			System.out.println("hi "+i);
			int j=1;
			while (j<=5) {
				System.out.println("hi "+i);
				
				j++;
			}
			i++;
		}

	}

}
